import logo from './logo.svg';
import './App.css';
import AppleBar from './components/AppleBar';
import {products} from './data/products';
import {cartProducts} from './data/cartProducts'
import MainBody from './components/MainBody/MainBody';
import { BrowserRouter, Route, Switch } from 'react-router-dom';
import CartScreen from './components/screens/CartScreen';
import ProductScreen from './components/screens/ProductScreen';
import AdminScreen from './components/screens/AdminScreen';
import { useDispatch, useSelector } from 'react-redux';
import { useEffect } from 'react';
import { GetProducts } from './store/reducers/productReducer';
import React from 'react';
import axios from 'axios'

function App() {
  const dispatch=useDispatch()
  const [products,setProducts]=React.useState([])
  const getProducts=async()=>{
    await axios.get('http://localhost:5000/addData').then((response)=>{
      setProducts(response.data)
      dispatch({type:'PRODUCT_LIST',payload:products})
  })
  }
  useEffect(()=>{
    getProducts()
  },[dispatch])
  console.log(products)
  return (
    <div className="App">

      <AppleBar />
      <BrowserRouter>
        <Switch>
          <Route exact path='/' component={()=><MainBody products={products} />} />
          <Route exact path='/cart' component={()=><CartScreen cartProducts={cartProducts} />} />
          <Route exact path='/products/:id' component={()=><ProductScreen />} />
          <Route exact path='/admin' component={()=><AdminScreen />} />
        </Switch>
      </BrowserRouter>
    </div>
  );
}

export default App;
