import React from 'react'
import Grid from '@material-ui/core/Grid';
import { makeStyles } from '@material-ui/core/styles';
import { Button } from '@material-ui/core';
import { useHistory, useParams } from 'react-router-dom';

import {products} from '../../data/products'
import { useDispatch } from 'react-redux';

const useStyles = makeStyles((theme) => ({
    root: {
      flexGrow: 1,
    },

  }));

const ProductScreen = () => {
    const classes = useStyles();
    const params= useParams()
    console.log(params)
    const history=useHistory()
    const dispatch=useDispatch()
    const addToCart=()=>{
        history.push('/cart')
        dispatch({type:'CART_ADD_ITEM',payload:products[params.id]})
    }
    return (
        <div className={classes.root}>
            <Grid container>

                <Grid item xs={7}>
                <img style={{height:"400px" }} src={products[params.id].image} alt="image" />
                </Grid>
                <Grid item xs={3}>
                    <div>{products[params.id].description } </div>
                    <div>Price:{products[params.id].price}</div>
                quantity
                    <div>Rating:{products[params.id].rating}</div>
                <Button onClick={addToCart} variant='contained' color='primary'>add to cart</Button>
                </Grid>

            </Grid>
    </div>
    )
}

export default ProductScreen
